mapper.select:
select userName
from tb_member
where userId = #{userId} and userPw = #{userPw}

select userName, userId, userEmail, userRegdate
from tb_member
where userId = #{userId} and userPw = #{userPw}

mapper.insert:
insert into tb_member (userId, userPw, userName, userEmail)
values(
#{userId}, #{userPw}, #{userName}, #{userEmail}
)
